$organization = ($env:SYSTEM_TEAMFOUNDATIONCOLLECTIONURI -replace "https://dev\.azure\.com/(.*)/", '$1').split('.')[0]

newman run AzureDevOps.Postman.postman_collection.json -e AzureDevOpsEnvironment.postman_environment.json --env-var "AccessToken=$env:SYSTEM_ACCESSTOKEN" --env-var "organization=$organization" --env-var "project=$env:System_TeamProject" -r "cli,json,htmlextra" --reporter-htmlextra-template "azureDevOpsStats.hbs" --reporter-htmlextra-export "Azure-DevOps-Report.html" --reporter-json-export "Azure-DevOps-Json.json"